import FilmRow from './FilmRow'
import React, { Component } from 'react'

class FilmListing extends Component {
	constructor(props) {
		super(props);
		this.state = {
			filter: 'all'

		}
	}
	render() {
		const allFilms = this.props.films.map(film => <FilmRow film={film} key={film.id} />)
		return (
			<div className="film-list">
				<h1 className="section-title">FILMS</h1>
				<div className="film-list-filters">

					<div className="film-list-filter.is-active" onClick={() => this.handleFilterClick('all')} >
						ALL
						< span className="section-count">{this.props.films.length}</span>
					</div>
					<div className={this.state.filter === 'faves' ? 'film-list-filter.is-active' : ''} onClick={() => this.handleFilterClick('faves')}>
						FAVES
                <span className="section-count">0</span>
					</div>
				</div>

				{ allFilms}
			</div >
		)
	}

	handleFilterClick = (filter) => {
		console.log("hhh", filter)
		this.setState({
			filter: filter

		})
		console.log('setting filter to and the filter argument');

	}

}

export default FilmListing
