import React, { Component } from 'react';
import './App.css';
import FilmDetails from './FilmDetails.js';
import FilmListing from './FilmListing.js';
import TMDB from './TMDB.js';
import axios from 'axios'

class App extends Component {

	constructor(props) {
		super(props)
		this.state = {
			films: TMDB.films,
			faves: [],
			current: {}
		}
	}
	handleFaveToggle = (film) => {
		const faves = this.state.faves.slice();
		const filmIndex = faves.indexOf(film);

		//If the film is already in their favorites, take it out of the faves array.
		if (filmIndex >= 0) {
			console.log(`Removing ${film.title} from faves`);
			faves.splice(filmIndex, 1);
		}
		//If the film is not in their favorites, add it to the faves array.
		if (filmIndex === -1) {
			console.log(`Adding ${film.title} to faves`);
			faves.push(film);
		}
		this.setState({
			faves: faves
		})
	}
	handleDetailsClick = (film) => {

		console.log('flim===>--', 'im clicked+', film.id)
		let flimid = film.id;
		const apiKey = process.env.REACT_APP_TMDB_API_KEY //getting the API_KEY stored in the .env file
		const url = `https://api.themoviedb.org/3/movie/${flimid}?api_key=${apiKey}&append_to_response=videos,images&language=en`

		//https://api.themoviedb.org/3/movie/550?api_key=f430c190c4739d691795094db5ad7706

		//let response = axios.get(`${process.env.REACT_APP_BASE_URL}?api_key=${process.env.REACT_APP_API_KEY}`,
		let response = axios.get(url,
			{
				headers: {
					'Content-Type': 'application/json'
				}
			})
			.then((response) => {
				console.log('the response i got===>', response);
				console.log(response.data.data[0].images.downsized.url);
				this.setState({
					url: response.data.data[0].images.downsized.url
				})
			})
			.catch((error) => {
				console.log('API ERROR:===>', error);
			});
		console.log(response);
		console.log("Fetching details for " + film.title)
		this.setState({
			current: film
		})
	}
	render() {
		return (
			<div className="film-library">
				<FilmListing films={this.state.films}
					onFaveToggle={this.handleFaveToggle}
					faves={this.state.faves}
					handleDetailsClick={this.handleDetailsClick}
					flim={this.state.current} />
				<FilmDetails film={this.state.current} />
			</div>
		);
	}
}

export default App;
