import React, { Component } from 'react'

//</div><div className={this.state.filter === 'faves' ? 'film-list-filter.is-active' : ''} 
class Fave extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isFave: false

		}
	}

	render() {
		const isFave = (this.state.isFave) ? 'remove_from_queue' : 'add_to_queue'
		console.log('isFave', isFave);

		return (

			<div className="film-row-fave add_to_queue" onClick={this.handleClick}>
				<p className="material-icons">{isFave ? 'add_to_queue' : 'remove_from_queue'}</p>

			</div>

		)
	}
	handleClick = (event) => {
		event.stopPropagation()
		this.setState({
			isFave: !this.state.isFave

		})


	}
}

export default Fave
