import React, { Component } from 'react';
import './App.css';
import FilmDetails from './FilmDetails.js';
import FilmListing from './FilmListing.js';
import TMDB from './TMDB.js';

class App extends Component {
	constructor(props) {
		super(props)
		this.state = {
			films: TMDB.films,
			faves: [],
			current: {}
		}
	}
	handleFaveToggle = (film) => {
		const faves = this.state.faves.slice();
		const filmIndex = faves.indexOf(film);

		//If the film is already in their favorites, take it out of the faves array.
		if (filmIndex >= 0) {
			console.log(`Removing ${film.title} from faves`);
			faves.splice(filmIndex, 1);
		}
		//If the film is not in their favorites, add it to the faves array.
		if (filmIndex === -1) {
			console.log(`Adding ${film.title} to faves`);
			faves.push(film);
		}
		this.setState({
			faves: faves
		})
	}
	handleDetailsClick = (film) => {
		console.log("Fetching details for " + film.title)
		this.setState({
			current: film
		})
	}
	render() {
		return (
			<div className="film-library">
				<FilmListing films={this.state.films} onFaveToggle={this.handleFaveToggle} faves={this.state.faves} />
				<FilmDetails film={this.state.current} />
			</div>
		);
	}
}

export default App;
