import React, { Component } from 'react';
import FlimDetails from './FlimDetails';
class FlimListing extends Component {
	render() {
		const allComments = this.props.comments.map((comment) => {
			return <Comment body={comment} />
		});
		return (
			<>
				<h2>{this.props.title}</h2>
				<p>by: {this.props.author}</p>
				<p>Date: {this.props.date.toDateString()}</p>
				<article>
					{this.props.body}
				</article>
				<p>Comments:</p>
				{allComments}
			</>
		)
	}
}
export default FlimListing;
